INSERT INTO SCOTT.MEDICINE (ID, MEDICINENO, BARCODE, MEDICINENAME, PINYINCODE, PRESCRIPTIONTYPEID, MEDICINESPECIFICATIONS, MEDICINESTATUS, MEDICINEDESCRIPTION, PURCHASEPRICE, RETAILPRICE, MANUFACTURERID, STOCK, CREATETIME) VALUES (2, '10002', '15648596321', '替硝唑氯化钠注射液', 'tixiaozuolvhuanazhusheye', 1, '6g*11袋/盒', 1, '替硝唑氯化钠注射液，适应症为替硝唑广泛用于各种厌氧菌感染：盆腔炎、腹膜炎、口腔炎、肛周脓肿、伪膜性结肠炎、溃疡性牙龈炎、糖尿病坏疽。预防术后感染：结肠和直肠手术后感染，如脓毒血症等；胃肠手术后及妇科手术后各种厌氧菌感染等', 200.00, 400.00, 2, 1702, TO_DATE('2020-06-05 14:42:05', 'YYYY-MM-DD HH24:MI:SS'));
